import { useRouter } from 'next/router';
import { gql, useLazyQuery } from '@apollo/client';
import { useState, useContext, useEffect } from 'react';
import AppContext from './context';
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Row,
  Col,
} from 'reactstrap';
function Dishes({
  restId,
  handleSetRestaurant,
  setIsReadyToFetch,
  isReadyToFetch,
}) {
  const [restaurantID, setRestaurantID] = useState();
  const { addItem } = useContext(AppContext);

  const GET_RESTAURANT_DISHES = gql`
    query ($id: ID!) {
      restaurant(id: $id) {
        id
        name
        dishes {
          id
          name
          description
          price
          image {
            url
          }
        }
      }
    }
  `;

  const router = useRouter();

  // const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
  //   variables: { id: restId },
  //   skip: !isReadyToFetch,
  //   onCompleted: (data) => {
  //     setIsReadyToFetch(false);
  //     handleSetRestaurant(data.restaurant);
  //   },
  // });

  const [loadData, { loading, error, data }] = useLazyQuery(
    GET_RESTAURANT_DISHES,
    {
      variables: { id: restId },
      onCompleted: (data) => {
        setIsReadyToFetch(false);
      },
    }
  );

  useEffect(() => {
    if (!loading) {
      loadData(); // Execute the query manually
    }
  }, [loading]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  console.log(data);
  // useEffect(() => setRestaurant(data.restaurant), [])

  let restaurant = data.restaurant;

  if (restId > 0) {
    return (
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'space-between',
        }}
      >
        {restaurant.dishes.map((res) => (
          <div style={{ padding: 0 }}>
            <Card style={{ width: 300, height: 561, margin: '0 10px 30px' }}>
              <CardImg
                top={true}
                style={{ height: 300, width: 300 }}
                src={`http://localhost:1337${res.image.url}`}
              />
              <CardBody>
                <CardTitle className="font-weight-bold">{res.name}</CardTitle>
                <CardText>{res.description}</CardText>
              </CardBody>
              <div
                className="card-footer"
                style={{ display: 'flex', justifyContent: 'space-between' }}
              >
                <div
                  style={{ paddingLeft: 15, paddingTop: 8, fontWeight: 'bold' }}
                >
                  ${res.price}
                </div>
                <Button
                  color="info"
                  outline
                  color="dark"
                  onClick={() => addItem(res)}
                >
                  + Add To Cart
                </Button>
              </div>
            </Card>
          </div>
        ))}
      </div>
    );
  } else {
    return <h1> No Dishes</h1>;
  }
}
export default Dishes;
