import { useRouter } from 'next/router';
import React, { useState, useCallback } from 'react';
import {
  ApolloProvider,
  ApolloClient,
  HttpLink,
  InMemoryCache,
  gql,
  useQuery,
} from '@apollo/client';
import RestaurantList from '../components/restaurantList';
import { InputGroup, InputGroupAddon, Input } from 'reactstrap';
import Dishes from '../components/dishes';

function Restaurant() {
  const router = useRouter();
  const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:1337';
  console.log(`URL: ${API_URL}`);
  const [query, setQuery] = useState('');
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });
  const [restaurant, setRestaurant] = useState({});
  const [isReadyToFetch, setIsReadyToFetch] = useState(true);

  const handleSetRestaurant = (restaurantData) => {
    setRestaurant(restaurantData);
  };

  return (
    <ApolloProvider client={client}>
      <div className="search">
        <h2 className="py-3"> {restaurant.name}</h2>
        <div>{restaurant.description}</div>
        <InputGroup className="py-3">
          <InputGroupAddon addonType="append"> Search </InputGroupAddon>
          <Input
            onChange={(e) => setQuery(e.target.value.toLocaleLowerCase())}
            value={query}
          />
        </InputGroup>
        <br></br>
      </div>
      <Dishes
        restId={router.query.restaurantId}
        handleSetRestaurant={handleSetRestaurant}
        setIsReadyToFetch={setIsReadyToFetch}
      >
      </Dishes>
    </ApolloProvider>
    // <p>Post: {router.query.restaurantId}</p>
  );
}

export default Restaurant;
